
public class StaticMethodPractice {
/*Static Method Practice
 * Hussein Karim
 *Prints two boxes
 */
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
box();            //prints first box 
box();            //prints second box
	}
	//Prints a box
public static void box(){
	System.out.println("****************" );
	System.out.println("*              *");
	System.out.println("*              *");
	System.out.println("*              *");
	System.out.println("****************" );
}
}
